sidebarCSS:
#root.root {--general-border-radius: 10px;}
#root.root {--general-margin: 4px;}

#root.root {--nav-btn-margin: 8px;}
#root.root {--nav-btn-width: 33px;}
#root.root {--nav-btn-height: 33px;}
#root.root {--nav-btn-len-margin: 3px;}

#root.root {--tabs-audio-btn-width: 22px;}
#root.root {--tabs-margin: 7px;}
#root.root {--tabs-height: 35px;}
#root.root {--tabs-inner-gap: 5px;}
#root.root {--tabs-border-radius: 10px;}


/* amount of pinned tabs in one row */
#root.root {--pinned-tabs-in-row: 3;}

/* pinned tab width calculation */
#root.root {--tabs-pinned-width: calc(
  (
    96vw 
    - (
      var(--tabs-margin) 
      * var(--pinned-tabs-in-row)
    )
  ) / var(--pinned-tabs-in-row)
);}

/* pinned tab height calculation */
#root.root{
  --tabs-pinned-height: calc(
    var(--tabs-pinned-width) 
    / 3.6 * var(--pinned-tabs-in-row) 
  ); !important
}

/* pinned tab audio icon */
.Tab[data-pin="true"] > .body > .audio { 
background: transparent;
box-shadow: none;
right: 3px;
top: 3px;
}

/* bigger pinned tab favicon */
.Tab[data-pin="true"] > .body > .fav > .fav-icon { 
  width: 30px;
  height: 30px;
  top: -7.5px;
  left: -6.5px; 
}

/* match badge to bigger icon */
.Tab[data-pin="true"] > .body > .fav > .badge{
  right: -8.5px;
  bottom: -7px;
}

/* pinned tab shadow, outline and background */
.Tab[data-pin="true"] .body {
	box-shadow: 0px 0px 9px #00000030;
  border: 1px solid var(--s-frame-bg);
  background-color: var(--toolbar-bg)
}

/* selected pinned tab outline */
.Tab[data-pin="true"][data-active="true"] .body 
{border: 1px solid var(--s-frame-fg)}

/* bottom margin */
.PinnedTabsBar {margin-bottom: 5px}

/* consistent navbar background */
#nav_bar {background-color: transparent !important;}

.fav::after{
  content: "";
  position: absolute;
  z-index: -999;

  border-radius: 5px;

  left: -2px;
  top: -2px;

  width: 20px;
  height: 20px;

  background-color: rgba(80, 80, 80, 0.1);

/* tab title text gradient */
.Tab .title {
	background: linear-gradient(90deg, var(--tabs-normal-fg) 70%, #f1f1f100 95%);
	background-clip: text;
	color: transparent;
}

/* remove padding on top */
#nav_bar{
  padding-top: 0px !important
}

